import{_ as a,o as s,c as e,Q as n}from"./chunks/framework.db0ccc71.js";const _=JSON.parse('{"title":"安装 Java JDK 并配置 JAVA_HOME","description":"","frontmatter":{},"headers":[],"relativePath":"notes/java.md"}'),o={name:"notes/java.md"},t=n('<h1 id="安装-java-jdk-并配置-java-home" tabindex="-1">安装 Java JDK 并配置 JAVA_HOME <a class="header-anchor" href="#安装-java-jdk-并配置-java-home" aria-label="Permalink to &quot;安装 Java JDK 并配置 JAVA_HOME&quot;">​</a></h1><h2 id="安装jdk" tabindex="-1">安装jdk <a class="header-anchor" href="#安装jdk" aria-label="Permalink to &quot;安装jdk&quot;">​</a></h2><p>下载地址: <a href="https://adoptium.net/zh-CN/temurin/releases/" target="_blank" rel="noreferrer">https://adoptium.net/zh-CN/temurin/releases/</a></p><h2 id="macos" tabindex="-1">MacOs <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;MacOs&quot;">​</a></h2><p>1.查找 <code>JAVA_HOME</code> 路径</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">$</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/usr/libexec/java_home</span></span></code></pre></div><p>2.编辑 <code>~/.zshrc</code> 文件</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">$</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">~/.zshrc</span></span></code></pre></div><p>3.添加到环境变量</p><div class="language-ini"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">export </span><span style="color:#F07178;">JAVA_HOME</span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;">/Library/Java/JavaVirtualMachines/temurin-17.jdk/Contents/Home</span></span></code></pre></div><p>4.让配置立即生效</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">$</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">source</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">~/.zshrc</span></span></code></pre></div><p>5.查看已插入的 <code>JAVA_HOME</code></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">$</span><span style="color:#A6ACCD;">  </span><span style="color:#C3E88D;">echo</span><span style="color:#A6ACCD;"> $JAVA_HOME</span></span></code></pre></div><h2 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h2><p>参考文档: <a href="https://www.jianshu.com/p/9fc41ea941aa" target="_blank" rel="noreferrer">Windows 配置 Java 环境变量</a></p><h2 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h2><p>待完善...</p>',18),l=[t];function p(c,r,i,d,h,u){return s(),e("div",null,l)}const m=a(o,[["render",p]]);export{_ as __pageData,m as default};
